pragma solidity ^0.5.2;

import "./Factory.sol";
import "./ProblemToken.sol";
import "openzeppelin-solidity/contracts/math/SafeMath.sol";
import "openzeppelin-solidity/contracts/token/ERC20/SafeERC20.sol";

contract ProblemTokenFactory is Factory {
    using SafeMath for uint256;
    using SafeERC20 for ProblemToken;

    function create(
        uint256 amount,
        string memory name, 
        string memory symbol, 
        uint8 decimals
    ) public returns (address problemTokenAddress) {
        ProblemToken problemToken = new ProblemToken(amount, name, symbol, decimals);
        problemToken.safeTransfer(msg.sender, amount);
        problemToken.transferOwnership(msg.sender);
        problemTokenAddress = address(problemToken);
        register(problemTokenAddress);
    }

}
